
import os
import pymorphy2
from collections import Counter
import math
import csv
import numpy as np


def preprocess_text(text):
    morph = pymorphy2.MorphAnalyzer(lang='ru')
    words = text.lower().split()
    filtered_words = []

    for word in words:
        parsed_word = morph.parse(word)[0]
        if ('NOUN' in parsed_word.tag or
            'VERB' in parsed_word.tag or 'INFN' in parsed_word.tag or
            'ADJF' in parsed_word.tag or 'ADJS' in parsed_word.tag):
            filtered_words.append(parsed_word.normal_form)

    return filtered_words

