def calculate_tf_idf(documents):
    # Считаем количество документов
    total_documents = len(documents)

    # Считаем частоту терминов в каждом документе
    tf = {}
    for doc_id, text in documents.items():
        tf[doc_id] = Counter(preprocess_text(text))

    # Считаем документную частоту каждого термина
    df = Counter()
    for doc_tf in tf.values():
        for term in doc_tf.keys():
            df[term] += 1

        # Вычисляем TF-IDF с нормализацией
    tf_idf_normalized = {}
    for doc_id, doc_tf in tf.items():
        doc_tf_idf = {}
        # Считаем ненормированные значения TF-IDF
        for term, freq in doc_tf.items():
            idf = math.log(total_documents / (1 + df[term]))
            doc_tf_idf[term] = freq * idf

        # Нормализуем значения TF-IDF
        norm = np.linalg.norm(list(doc_tf_idf.values()))
        for term in doc_tf_idf:
            doc_tf_idf[term] /= norm  # L2-нормализация

        tf_idf_normalized[doc_id] = doc_tf_idf

    return tf_idf_normalized

