def save_tf_idf_to_csv(tf_idf, file_path):
    with open(file_path, mode='w', newline='', encoding='utf-8') as file:
        writer = csv.writer(file)
        header = ['Document', 'Term', 'TF-IDF']
        writer.writerow(header)

        for doc_id, doc_tf_idf in tf_idf.items():
            for term, value in doc_tf_idf.items():
                writer.writerow([doc_id, term, value])

# Использование функции для сохранения TF-IDF в CSV-файл
save_tf_idf_to_csv(tf_idf, 'tf_idf.csv')

import csv

def split_and_combine_csv(input_csv, output_folder):
    # Reading the input CSV
    with open(input_csv, 'r', encoding='utf-8') as file:
        reader = csv.reader(file)
        header = next(reader)  # Read header

        # Initialize a dictionary to hold data for each combined file
        combined_data = {f"tf_idf{i}.csv": [] for i in range(11)}

        for row in reader:
            doc_id = row[0]
            file_index = int(doc_id[-1])  # Extract the last character of the document ID and convert to int
            file_name = f"tf_idf{file_index}.csv"

            if not combined_data[file_name]:
                combined_data[file_name].append(header)  # Add header for the first time

            combined_data[file_name].append(row)

    # Writing combined data to separate files
    for file_name, rows in combined_data.items():
        output_path = f"{output_folder}/{file_name}"
        with open(output_path, 'w', encoding='utf-8', newline='') as file:
            writer = csv.writer(file)
            writer.writerows(rows)

# Example usage
split_and_combine_csv("tf_idf.csv", "/content/rrr")
